SELECT a.TITLE, a.BOARD_ID, b.REPLY_ID, b.WRITER_ID, b.CONTENTS, DATE_FORMAT(b.CREATED_DATE, '%Y-%m-%d') FROM USED_GOODS_BOARD AS a INNER JOIN USED_GOODS_REPLY AS b ON a.BOARD_ID = b.BOARD_ID WHERE a.CREATED_DATE between '2022-10-01' and '2022-10-31' ORDER BY b.CREATED_DATE ASC, a.TITLE ASC;